import React from "react";
import {Link} from "react-router-dom";
import axios from "axios";

const Exercise = props => (
    <tr>
      <td>{props.exercise.username}</td>
      <td>{props.exercise.description}</td>
      <td>{props.exercise.duration}</td>
      <td>{props.exercise.date.substring(0,10)}</td>
      <td>
        <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
      </td>
    </tr>
  )

//we have used substring in date bcz date method will return all date, time, timezone and we need only atarting 
//10 characters so we have removed everything else  
class ExerciseList extends React.Component{
    constructor(props){
        super(props)

        this.deleteExercise = this.deleteExercise.bind(this);

        this.state = {
            exercise : []
        }
    }

    componentDidlMount(){
        axios.get('http://localhost:5000/exercises/')
        .then(response =>{
            this.setState =({
                exercises : response.data
            })
            .catch((error)=>{console.log(error)})
        })
    }

    deleteExercise(id){
        axios.delete('http://localhost/exercises'+id)
        .then(response=>{console.log(response.data)})
        this.setState({
            exercises: this.state.exercises.filter(el=> el._id !== id)
            //filter will return those exercises which are not equal to delete id
            // and _id is id that is automatically created in mongodb(plz check mongo db)
        })
    }

    exerciseList(){
        return this.state.exercise.map(currentexercise =>{
            return <Exercise exercise={currentexercise} daleteExercise ={this.deleteExercise} key={currentexercise._id} />
        })
    }

    render(){
        return (
            <div className="conatiner">
                <h3>Logged Exercises</h3>
                    <table className="table">
                    <thead className="thead-light">
                        <tr>
                        <th>Username</th>
                        <th>Description</th>
                        <th>Duration</th>
                        <th>Date</th>
                        <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.exerciseList() }
                    </tbody>
                    </table>
            </div>
        )
    }
}

export default ExerciseList;
